// Hole den aktuellen SoC-Wert
let currentSoC = msg.payload.batteryLevel;

// Hole die Historie und den letzten gespeicherten Tag aus dem Flow Context
let socHistory = flow.get('socHistory') || [];
let lastSavedDate = flow.get('lastSavedDate') || '';

// Hole den Sonnenuntergangszeitstempel
let sunsetTimestamp = msg.payload.sunsetTimestamp; // Erwartet im Format "HH:MM"

// Berechne die Zeit bis zum Sonnenuntergang
let sunsetDate = new Date();
let [sunsetHours, sunsetMinutes] = sunsetTimestamp.split(':').map(Number);
sunsetDate.setHours(sunsetHours, sunsetMinutes, 0, 0);

// √úberpr√ºfe, ob der aktuelle SoC 100% betr√§gt und ob heute ein neuer Tag ist
let today = new Date().toISOString().split('T')[0]; // Nur das Datum (YYYY-MM-DD)
if (currentSoC === 100 && today !== lastSavedDate) {
    // Speichere den aktuellen Zeitstempel
    let currentTime = new Date().toISOString();

    // F√ºge den neuen Eintrag zur Historie hinzu
    socHistory.push({ time: currentTime, value: currentSoC });

    // Begrenze die Historie auf die letzten 30 Eintr√§ge
    if (socHistory.length > 30) {
        socHistory.shift(); // Entferne den √§ltesten Eintrag
    }

    // Speichere die aktualisierte Historie und den letzten gespeicherten Tag im Flow Context
    flow.set('socHistory', socHistory);
    flow.set('lastSavedDate', today);
} else if (today !== lastSavedDate && new Date() > sunsetDate) {
    // Nach Sonnenuntergang: Speichere den aktuellen SoC, wenn nicht bereits 100% gespeichert wurde
    if (!socHistory.some(entry => entry.value === 100 && entry.time.split('T')[0] === today)) {
        // Speichere den aktuellen Zeitstempel
        let currentTime = new Date().toISOString();

        // F√ºge den neuen Eintrag zur Historie hinzu
        socHistory.push({ time: currentTime, value: currentSoC });

        // Begrenze die Historie auf die letzten 30 Eintr√§ge
        if (socHistory.length > 30) {
            socHistory.shift(); // Entferne den √§ltesten Eintrag
        }

        // Speichere die aktualisierte Historie und den letzten gespeicherten Tag im Flow Context
        flow.set('socHistory', socHistory);
        flow.set('lastSavedDate', today);
    }
}

// Gebe die Nachricht weiter
return msg;

// Aktuelle Werte aus dem Flow abrufen
let socHistory = flow.get('socHistory') || [];
let reducedChargeDaysHistory = flow.get('reducedChargeDaysHistory') || []; // Initialisiere, falls nicht existiert

let fullChargeThreshold = global.get('FULL_CHARGE_THRESHOLD') || 100;
let reducedChargeThreshold = 95;
let maxChargeThreshold = 100; // Maximale Ladegrenze

// Variable: Anzahl der Tage mit reduzierter Ladegrenze
let reducedChargeDays = flow.get('reducedChargeDays') || 4; // Standardm√§√üig 4 Tage

// Aktuelles Datum
let today = new Date();

// Zeitpunkt berechnen, ab dem gepr√ºft wird
let reducedChargeStart = new Date(today.getTime() - reducedChargeDays * 24 * 60 * 60 * 1000);

// Funktion: Wurde der Akku innerhalb der reducedChargeDays auf 100% geladen?
function wasFullyChargedRecently() {
    return socHistory.some(entry =>
        Number(entry.value) === maxChargeThreshold &&
        new Date(entry.time) >= reducedChargeStart
    );
}

// Funktion: Hat der Akku innerhalb der reducedChargeDays die reduzierte Ladegrenze erreicht?
function wasReducedChargeRecently() {
    return reducedChargeDaysHistory.some(entry =>
        new Date(entry.time) >= reducedChargeStart
    );
}

// Letzten SOC-Eintrag abrufen
let lastSocEntry = socHistory.length > 0 ? socHistory[socHistory.length - 1] : null;
let lastSocDate = lastSocEntry ? new Date(lastSocEntry.time) : null;

/*
// Debug-Ausgaben
// node.warn("Letzter SOC-Eintrag: " + JSON.stringify(lastSocEntry));
// node.warn("ReducedChargeDaysHistory: " + JSON.stringify(reducedChargeDaysHistory));
*/

// Pr√ºfen, ob der letzte SOC-Eintrag von gestern ist
let yesterday = new Date(today);
yesterday.setDate(yesterday.getDate() - 1);

let isSameDate = lastSocDate &&
    lastSocDate.getFullYear() === yesterday.getFullYear() &&
    lastSocDate.getMonth() === yesterday.getMonth() &&
    lastSocDate.getDate() === yesterday.getDate();

// Sicherstellen, dass die Werte Zahlen sind
let lastSocValue = lastSocEntry ? Number(lastSocEntry.value) : null;

/*
// Weitere Debug-Ausgaben
// node.warn("Ist der letzte SOC-Eintrag von gestern? " + isSameDate);
// node.warn("Letzter SOC-Wert: " + lastSocValue);
*/

// Toleranzwerte definieren
let tolerance = 2; // Toleranz in Prozent

// Wenn der Akku gestern nahe 100% erreicht hat, Ladegrenze auf reducedChargeThreshold setzen
if (
    lastSocEntry &&
    lastSocValue >= maxChargeThreshold - tolerance &&
    isSameDate
) {
    // Ladegrenze auf reducedChargeThreshold setzen
    fullChargeThreshold = reducedChargeThreshold;
    /*
    node.warn(`üîã Akku hat gestern ${lastSocValue}% erreicht - Ladegrenze auf ${reducedChargeThreshold}% gesetzt`);
    */

    // ReducedChargeDays-History aktualisieren
    reducedChargeDaysHistory.push({ time: today.toISOString() });

    // Alte Eintr√§ge entfernen, die au√üerhalb des reducedChargeDays-Fensters liegen
    reducedChargeDaysHistory = reducedChargeDaysHistory.filter(entry =>
        new Date(entry.time) >= reducedChargeStart
    );

    // Aktualisierte History im Flow speichern
    flow.set('reducedChargeDaysHistory', reducedChargeDaysHistory);
    // node.warn("Aktualisierte ReducedChargeDaysHistory: " + JSON.stringify(reducedChargeDaysHistory));

} else {
    // node.warn("Akku hat gestern keine 100% erreicht");

    // Wenn die Ladegrenze derzeit reduziert ist und die reduzierte Ladegrenze nicht k√ºrzlich erreicht wurde, zur√ºck auf max setzen
    if (fullChargeThreshold !== maxChargeThreshold && !wasReducedChargeRecently()) {
        fullChargeThreshold = maxChargeThreshold;
        // node.warn(`üîÑ Ladegrenze zur√ºck auf ${maxChargeThreshold}% gesetzt`);

        // ReducedChargeDays-History zur√ºcksetzen
        reducedChargeDaysHistory = [];
        flow.set('reducedChargeDaysHistory', reducedChargeDaysHistory);
        // node.warn("ReducedChargeDaysHistory wurde zur√ºckgesetzt.");
    } else {
        // node.warn("Ladegrenze bleibt unver√§ndert bei " + fullChargeThreshold + "%");
    }
}

// Aktuelle Ladegrenze im Flow speichern
global.set('FULL_CHARGE_THRESHOLD', fullChargeThreshold);
// node.warn("Aktuelle Ladegrenze nach Setzen: " + global.get('FULL_CHARGE_THRESHOLD'));

// Nachricht zur√ºckgeben
msg.payload = fullChargeThreshold;
return msg;

// L√∂sche die Historie und den letzten gespeicherten Tag
flow.set('socHistory', []);
flow.set('lastSavedDate', '');
return msg;

// Hole die SOC Historie aus dem Flow Context
let socHistory = flow.get('socHistory') || [];

// Gebe die Historie aus
msg.payload = socHistory;
return msg;

// Hole das heutige Datum und die Zeit
let today = new Date();
let threeDaysAgo = new Date(today);
threeDaysAgo.setDate(today.getDate() - 3);

// Historie abrufen
let socHistory = flow.get('socHistory') || [];

// Setze den SOC f√ºr die letzten drei Tage auf 100%
for (let i = 0; i < 3; i++) {
    let dateToSet = new Date(threeDaysAgo);
    dateToSet.setDate(threeDaysAgo.getDate() + i);

    // √úberpr√ºfen, ob der Eintrag f√ºr dieses Datum bereits existiert
    let existingEntryIndex = socHistory.findIndex(entry => 
        new Date(entry.time).toISOString().split('T')[0] === dateToSet.toISOString().split('T')[0]);

    if (existingEntryIndex !== -1) {
        // Wenn der Eintrag bereits existiert, aktualisiere den Wert
        socHistory[existingEntryIndex].value = 100;
    } else {
        // Ansonsten neuen Eintrag hinzuf√ºgen
        socHistory.push({
            time: dateToSet.toISOString(),
            value: 100
        });
    }
}

// Begrenze die Historie auf die letzten 30 Eintr√§ge
if (socHistory.length > 30) {
    socHistory.shift(); // Entferne den √§ltesten Eintrag
}

// Speichere die aktualisierte Historie im Flow Context
flow.set('socHistory', socHistory);

// Gebe die Nachricht weiter
return msg;

// Hole die Konstanten aus dem Flow
const lowBatteryThreshold = flow.get('LOW_BATTERY_THRESHOLD');
const fullChargeThreshold = flow.get('FULL_CHARGE_THRESHOLD');
const noChargeDays = flow.get('NO_CHARGE_DAYS');
const minOutputPower = flow.get('MIN_OUTPUT_POWER');
const mediumOutputPower = flow.get('MEDIUM_OUTPUT_POWER');
const highOutputPower = flow.get('HIGH_OUTPUT_POWER');
const outputPowerThreshold = flow.get('OUTPUT_POWER_THRESHOLD');
const maxOutputPower = flow.get('MAX_OUTPUT_POWER');
const outputPowerStep = flow.get('OUTPUT_POWER_STEP');
const hysteresis = flow.get('HYSTERESIS');
const minEinspeisung = flow.get('MIN_EINSPEISUNG');
const startACLeistung = flow.get('START_AC_LEISTUNG');
const maxACLeistung = flow.get('MAX_AC_LEISTUNG');
const maxGesamtLeistung = flow.get('MAX_GESAMTLEISTUNG');
const leistungsschritt = flow.get('LEISTUNGSSCHRITT');
const entPrellzeit = flow.get('ENT_PRELLZEIT');
const skalierung = flow.get('SKALIERUNG');
const batteryFullThreshold = flow.get('BATTERY_FULL_THRESHOLD');
const steigerschritt = flow.get('STEIGERUNGSSCHRITT');
const zielNegativeLeistung = flow.get('ZIEL_NEGATIVE_LEISTUNG');
const reducedChargeDays = flow.get('reducedChargeDays');

// Sende alle Variablen an die Debug-Node
msg.payload = {
    LOW_BATTERY_THRESHOLD: lowBatteryThreshold,
    FULL_CHARGE_THRESHOLD: fullChargeThreshold,
    NO_CHARGE_DAYS: noChargeDays,
    MIN_OUTPUT_POWER: minOutputPower,
    MEDIUM_OUTPUT_POWER: mediumOutputPower,
    HIGH_OUTPUT_POWER: highOutputPower,
    OUTPUT_POWER_THRESHOLD: outputPowerThreshold,
    MAX_OUTPUT_POWER: maxOutputPower,
    OUTPUT_POWER_STEP: outputPowerStep,
    HYSTERESIS: hysteresis,
    MIN_EINSPEISUNG: minEinspeisung,
    START_AC_LEISTUNG: startACLeistung,
    MAX_AC_LEISTUNG: maxACLeistung,
    MAX_GESAMTLEISTUNG: maxGesamtLeistung,
    LEISTUNGSSCHRITT: leistungsschritt,
    ENT_PRELLZEIT: entPrellzeit,
    SKALIERUNG: skalierung,
    BATTERY_FULL_THRESHOLD: batteryFullThreshold,
    STEIGERUNGSSCHRITT: steigerschritt,
    ZIEL_NEGATIVE_LEISTUNG: zielNegativeLeistung,
    TAGE_MIT_REDUZIERTER_LADUNG: reducedChargeDays
};

return msg;

// Extrahiere die SOC-Werte aus msg.payload
let soc1 = msg.payload.SOC_1;
let soc2 = msg.payload.SOC_2;
let soc3 = msg.payload.SOC_3;

// Den kleinsten Wert ermitteln
let minSOC = Math.min(soc1, soc2, soc3);

// Sicherstellen, dass msg.minSOC hinzugef√ºgt wird
msg.payload.minSOC = minSOC;

// Nachricht zur√ºckgeben
return msg;

// Pr√ºfe, ob es schon eine Flagge f√ºr den Status "open" gibt
if (global.get('gate_open') !== true && msg.payload === "open") {
    // Setze die Ladeleistung auf 0
    msg.payload = 0;

    // Setze die Flagge auf "true", um zu verhindern, dass dies erneut passiert
    global.set('gate_open', true);

    return msg;
} 
// Wenn der Zustand auf "close" wechselt, setze die Flagge zur√ºck
else if (msg.payload === "close") {
    global.set('gate_open', false);
}

// Falls der Zustand noch nicht gewechselt ist oder die Flagge gesetzt ist, nichts tun
return null;

// Konstanten f√ºr die Sonnenaufgangs- und Sonnenuntergangszeiten
const sunriseTime = msg.payload.sunriseTimestamp;  // Sonnenaufgangszeit (hh:mm)
const sunsetTime = msg.payload.sunsetTimestamp;    // Sonnenuntergangszeit (hh:mm)

// Hilfsfunktion, um Zeit zu vergleichen
function isTimeInRange(currentTime, startTime, endTime) {
    let current = new Date("1970-01-01T" + currentTime + ":00Z");
    let start = new Date("1970-01-01T" + startTime + ":00Z");
    let end = new Date("1970-01-01T" + endTime + ":00Z");

    return current >= start && current <= end;
}

// Aktuelle Zeit als 'hh:mm'
let now = new Date();
let currentTimeString = now.toTimeString().slice(0, 5);  // Extrahiere die aktuelle Zeit im Format 'hh:mm'

// Aktueller Schaltzustand des Switches
let switchState = msg.payload.switchState;

// Logik zum Sonnenaufgang: Schalter auf 1 setzen und pr√ºfen
if (isTimeInRange(currentTimeString, sunriseTime, sunsetTime)) {
    if (switchState !== 1) {
        // Schalter auf 1 setzen, wenn er falsch steht
        switchState = 1;
        msg.payload = 1;  // Nur die Zahl 1 ausgeben
        node.send(msg);
    }
} else {
    // Logik nach Sonnenuntergang: Schalter auf 2 setzen und pr√ºfen
    if (switchState !== 2) {
        // Schalter auf 2 setzen, wenn er falsch steht
        switchState = 2;
        msg.payload = 2;  // Nur die Zahl 2 ausgeben
        node.send(msg);
    }
}

// Konstante Werte f√ºr Schwellenwerte
const LOW_BATTERY_THRESHOLD = flow.get('LOW_BATTERY_THRESHOLD') || 20; // Schwellenwert f√ºr niedrigen Akku
const FULL_CHARGE_THRESHOLD = flow.get('FULL_CHARGE_THRESHOLD') || 100; // Schwellenwert f√ºr volle Ladung
const NO_CHARGE_DAYS = flow.get('NO_CHARGE_DAYS') || 3; // Anzahl der Tage ohne volle Ladung
const MIN_OUTPUT_POWER = flow.get('MIN_OUTPUT_POWER') || 30; // Mindestleistung 30 Watt
const MEDIUM_OUTPUT_POWER = flow.get('MEDIUM_OUTPUT_POWER') || 60; // Step auf 60 Watt
const HIGH_OUTPUT_POWER = flow.get('HIGH_OUTPUT_POWER') || 90; // Step auf 90 Watt
const OUTPUT_POWER_THRESHOLD = flow.get('OUTPUT_POWER_THRESHOLD') || 100; // Step auf 100 Watt
const MAX_OUTPUT_POWER = flow.get('MAX_OUTPUT_POWER') || 800; // Maximalleistung
const OUTPUT_POWER_STEP = flow.get('OUTPUT_POWER_STEP') || 1; // Schrittgr√∂√üe √ºber 100 Watt
const HYSTERESIS = flow.get('HYSTERESIS') || 50; // Hysterese von 50 Watt

const batteryLevel = msg.payload.batteryLevel;
const sunsetTimeString = msg.payload.sunsetTimestamp;
const sunriseTimeString = msg.payload.sunriseTimestamp;
const currentPower = msg.payload.currentPower;
const socHistory = flow.get('socHistory') || [];

// Debug: Historie anzeigen
//node.warn("SOC History: " + JSON.stringify(socHistory));

const today = new Date();
const sunsetHours = parseInt(sunsetTimeString.split(':')[0], 10);
const sunsetMinutes = parseInt(sunsetTimeString.split(':')[1], 10);
const sunriseHours = parseInt(sunriseTimeString.split(':')[0], 10);
const sunriseMinutes = parseInt(sunriseTimeString.split(':')[1], 10);

const sunset = new Date(today.getFullYear(), today.getMonth(), today.getDate(), sunsetHours, sunsetMinutes);
const sunrise = new Date(today.getFullYear(), today.getMonth(), today.getDate(), sunriseHours, sunriseMinutes);
const sunsetTimestamp = sunset.getTime();
const sunriseTimestamp = sunrise.getTime();
const currentTime = new Date().getTime();

// √úberpr√ºfen, ob der Akku an den letzten drei Tagen weniger als 100% geladen wurde
const threeDaysAgo = new Date();
threeDaysAgo.setDate(today.getDate() - NO_CHARGE_DAYS);

// Letzte 3 Tage aus der Historie filtern
const recentEntries = socHistory.filter(entry => new Date(entry.time).getTime() > threeDaysAgo.getTime());

// Debug: Zeige die gefilterten Eintr√§ge der letzten 3 Tage
//node.warn("Filtered entries (last 3 days): " + JSON.stringify(recentEntries));

// Pr√ºfen, ob in den letzten drei Tagen nicht 100 % erreicht wurde
const noFullChargeInThreeDays = recentEntries.every(entry => entry.value < FULL_CHARGE_THRESHOLD);

// Debug: Zeige das Ergebnis der √úberpr√ºfung
//node.warn("No full charge in last 3 days: " + noFullChargeInThreeDays);

// Wenn in den letzten 3 Tagen keine 100% erreicht wurden oder wenn der Akku unter 30% ist
if (noFullChargeInThreeDays || batteryLevel <= LOW_BATTERY_THRESHOLD) {
    msg.payload = 0; // Keine Leistung abgeben, nur laden
    //    node.warn("No discharge allowed. Either no full charge in last 3 days or battery level below 30%.");
    return msg;
}

// Hole den zuletzt gesetzten Wert f√ºr die Leistung, fallback auf 0
let lastSetPower = flow.get('lastSetPower') || 0;

// Debug: Zeige den aktuellen Wert von lastSetPower
//node.warn("Last set power: " + lastSetPower);

// Normaler Entladungsbetrieb (wenn der Akku in den letzten 3 Tagen 100 % erreicht hat)
if (currentTime > sunsetTimestamp || currentTime < sunriseTimestamp) {
    let requiredPower;

    if (currentPower > 0) {
        // Wenn Strom bezogen wird, addiere den aktuellen Verbrauch zu lastSetPower
        requiredPower = lastSetPower + currentPower;
    } else {
        // Berechne die erforderliche Leistung, um den Bezug auf 0 Watt zu bringen
        requiredPower = lastSetPower + currentPower;
    }

    // Anwendung der Hysterese
    if (requiredPower < lastSetPower - HYSTERESIS) {
        requiredPower = lastSetPower - HYSTERESIS; // Nach unten anpassen
    } else if (requiredPower > lastSetPower + HYSTERESIS) {
        requiredPower = lastSetPower + HYSTERESIS; // Nach oben anpassen
    }

    // Leisungsspr√ºnge anwenden
    if (requiredPower < MIN_OUTPUT_POWER) {
        requiredPower = MIN_OUTPUT_POWER;
    } else if (requiredPower < MEDIUM_OUTPUT_POWER) {
        requiredPower = MEDIUM_OUTPUT_POWER;
    } else if (requiredPower < HIGH_OUTPUT_POWER) {
        requiredPower = HIGH_OUTPUT_POWER;
    } else if (requiredPower < OUTPUT_POWER_THRESHOLD) {
        requiredPower = OUTPUT_POWER_THRESHOLD; // Erlaubt 100 Watt
    } else if (requiredPower > MAX_OUTPUT_POWER) {
        requiredPower = MAX_OUTPUT_POWER;
    } else if (requiredPower >= OUTPUT_POWER_THRESHOLD) {
        // Wenn requiredPower 100 oder mehr ist, in Schritten erh√∂hen
        requiredPower = Math.ceil((requiredPower - OUTPUT_POWER_THRESHOLD) / OUTPUT_POWER_STEP) * OUTPUT_POWER_STEP + OUTPUT_POWER_THRESHOLD;
        if (requiredPower > MAX_OUTPUT_POWER) {
            requiredPower = MAX_OUTPUT_POWER; // Begrenzung auf das Maximum
        }
    }

    // Setze die erforderliche Leistung
    msg.payload = requiredPower;

    // Aktualisiere den lastSetPower im Flow-Kontext
    flow.set('lastSetPower', requiredPower);

    //    node.warn("Discharging battery with required power: " + requiredPower);
    return msg;
} else {
    // Wenn au√üerhalb der Entladezeiten, setze Leistung auf 0
    msg.payload = 0; // Keine Leistung abgeben
    //    node.warn("Outside allowed discharge times. No discharge.");
    return msg;
}

// Konstanten f√ºr die Logik
const MIN_EINSPEISUNG = flow.get('MIN_EINSPEISUNG') || -150;  // Mindesteinspeisung in Watt, um das AC-Laden zu starten (negative Werte, da Einspeisung)
const START_AC_LEISTUNG = flow.get('START_AC_LEISTUNG') || 100; // Startwert f√ºr das AC-Laden in Watt
const MAX_AC_LEISTUNG = flow.get('MAX_AC_LEISTUNG') || 900;   // Maximale Ladeleistung des AC-Moduls in Watt
const MAX_GESAMTLEISTUNG = flow.get('MAX_GESAMTLEISTUNG') || 1100; // Maximale Ladeleistung des Systems in Watt (AC + BKW)
const ENT_PRELLZEIT = flow.get('ENT_PRELLZEIT') || 60000;   // Entprellzeit in Millisekunden (60 Sekunden)
const SKALIERUNG = flow.get('SKALIERUNG') || 1;          // Skalierung der Einspeisung
// **Angepasste Zeile:**
const BATTERY_FULL_THRESHOLD = global.get('FULL_CHARGE_THRESHOLD') || 100; // Schwellenwert f√ºr den vollen Akku in Prozent
const ZIEL_NEGATIVE_LEISTUNG = flow.get('ZIEL_NEGATIVE_LEISTUNG') || -100; // Zielwert f√ºr die negative Einspeisung

// PID-Konstanten
const Kp = 0.1;  // Proportionalfaktor
const Ki = 0.05; // Integralwert
const Kd = 0.01; // Derivatwert

// Variablen zur Speicherung der letzten √Ñnderung
let lastChangeTime = context.get('lastChangeTime') || 0;
let currentACLeistung = context.get('currentACLeistung') || 0;
let previousError = context.get('previousError') || 0;
let integral = context.get('integral') || 0;

// Sonnenaufgangs- und -untergangszeiten (im Format 'hh:mm')
const sunriseTime = msg.payload.sunriseTimestamp;  // Sonnenaufgang hh:mm
const sunsetTime = msg.payload.sunsetTimestamp;    // Sonnenuntergang hh:mm

// Hilfsfunktion, um Zeit zu vergleichen
function isTimeInRange(currentTime, startTime, endTime) {
    let current = new Date("1970-01-01T" + currentTime + ":00Z");
    let start = new Date("1970-01-01T" + startTime + ":00Z");
    let end = new Date("1970-01-01T" + endTime + ":00Z");

    return current >= start && current <= end;
}

// Aktueller Zeitstempel
let now = new Date();
let currentTimeString = now.toTimeString().slice(0, 5);  // Extrahiere die aktuelle Zeit im Format 'hh:mm'

// Eingangswert f√ºr die Einspeisung (negative Werte zeigen Einspeisung)
let currentpower = msg.payload.currentPower;  // Annahme: msg.payload enth√§lt currentPower vom Z√§hler
let bkwPower = msg.payload.PV_Power; // Leistung des Balkonkraftwerks
let batteryLevel = msg.payload.batteryLevel; // Aktueller Ladezustand des Akkus
let AC_Power = msg.payload.AC_Power; // Hier den neuen AC_Power Wert verwenden

// Fallback: Pr√ºfen, ob wichtige Werte vorhanden sind
if (typeof currentpower === 'undefined' || typeof bkwPower === 'undefined' || typeof batteryLevel === 'undefined') {
    currentACLeistung = 0;
    msg.payload = currentACLeistung;  // Setze die Ladeleistung auf 0
    node.send(msg);
    return;  // Beende die Ausf√ºhrung, da die notwendigen Werte fehlen
}

// Wenn der Akku voll ist, stoppe die Ladung
if (batteryLevel >= BATTERY_FULL_THRESHOLD) {
    currentACLeistung = 0;
    msg.payload = currentACLeistung;
    node.send(msg);  // Senden der Nachricht
    return;  // Beende die Ausf√ºhrung
}

// √úberpr√ºfen, ob die aktuelle Zeit zwischen Sonnenaufgang und Sonnenuntergang liegt
if (isTimeInRange(currentTimeString, sunriseTime, sunsetTime)) {
    // Pr√ºfen, ob die Entprellzeit seit der letzten √Ñnderung abgelaufen ist
    if (now.getTime() - lastChangeTime >= ENT_PRELLZEIT) {

        // **Logik zur Ladeleistungserh√∂hung bei negativer Einspeisung**
        if (currentpower <= MIN_EINSPEISUNG && Math.abs(currentpower) > currentACLeistung) {
            // Berechne die verf√ºgbare Leistung zur AC-Ladung
            let availablePower = Math.abs(currentpower * SKALIERUNG) - Math.abs(MIN_EINSPEISUNG);

            // Bestimme die neue AC-Ladeleistung basierend auf der Einspeisung
            let newACLeistung = Math.min(
                START_AC_LEISTUNG + availablePower,
                MAX_AC_LEISTUNG
            );

            // Berechne die maximale AC-Ladeleistung unter Ber√ºcksichtigung der Gesamtladeleistung
            let gesamtLeistung = bkwPower + newACLeistung;
            if (gesamtLeistung > MAX_GESAMTLEISTUNG) {
                newACLeistung = Math.max(0, MAX_GESAMTLEISTUNG - bkwPower);
            }

            // Wenn sich die AC-Leistung ge√§ndert hat
            if (newACLeistung > currentACLeistung) {
                currentACLeistung = newACLeistung;
                lastChangeTime = now.getTime();
                context.set('currentACLeistung', currentACLeistung);
                context.set('lastChangeTime', lastChangeTime);
                msg.payload = currentACLeistung;  // Setzt den Payload auf den berechneten Wert
                node.send(msg);  // Senden der Nachricht
            }
        }

        // **Dynamische Reduktion der AC-Leistung, um Einspeisung nahe -100W zu halten, wenn `currentpower` positiv wird**
        else if (currentpower > 0) {
            // Berechnung des Fehlers und PID-Regler
            let error = ZIEL_NEGATIVE_LEISTUNG - currentpower;  // Fehler: Differenz zwischen Zielwert und aktueller Leistung
            integral += error;  // Integral
            let derivative = error - previousError;  // Derivat (√Ñnderung des Fehlers)

            // PID-Regler Berechnung
            let pidAdjustment = Kp * error + Ki * integral + Kd * derivative;

            // Berechne die neue AC-Leistung
            let newACLeistung = Math.max(0, currentACLeistung - Math.round(pidAdjustment)); // PID angepasst, mit Abrundung f√ºr ganze Zahlen

            // Setze neue AC-Leistung, wenn sich etwas ge√§ndert hat
            if (newACLeistung !== currentACLeistung) {
                currentACLeistung = newACLeistung;
                context.set('currentACLeistung', currentACLeistung);
                previousError = error;  // Aktualisiere den vorherigen Fehler f√ºr den n√§chsten Durchlauf
                context.set('previousError', previousError);
                integral = integral;  // Speichern des Integralwertes f√ºr den n√§chsten Durchlauf
                msg.payload = currentACLeistung;
                node.send(msg);
            }
        }
    }
} else {
    // Falls au√üerhalb des Zeitfensters, Ladeleistung auf 0 setzen
    currentACLeistung = 0;
    msg.payload = currentACLeistung;  // Setze die Ladeleistung auf 0
    node.send(msg);  // Weitergabe der Nachricht
}
